mapper.select:
SELECT IFNULL(SUM(t.reps_target * e.default_calories_per_set), 0)
FROM todos t
         JOIN exercises e ON t.exercise_id = e.exercise_id
WHERE t.date = #{date,jdbcType=DATE}
  AND t.user_id = #{userId,jdbcType=BIGINT}
  AND t.is_completed = TRUE

SELECT exercise_id AS exerciseId, name AS name, default_calories_per_set AS defaultCaloriesPerSet, unit AS unit FROM exercises
name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY name ASC LIMIT #{limit} OFFSET #{offset}
