mapper.update:
UPDATE todos
SET is_done = #{isDone}
WHERE date = #{date};

mapper.select:
SELECT COUNT(*) FROM todos
WHERE date = #{date}
  AND (is_completed IS NULL OR is_completed = FALSE)

mapper.update:
UPDATE todos
SET rest_time = #{restTime},
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

UPDATE todos
SET rest_time = NULL,
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};
