mapper.insert:
INSERT INTO todos (
    user_id, exercise_id, date, sets_number, reps_target, rest_time, is_completed, is_done
)
VALUES (
           1,
           #{exerciseId},
           #{date},
           #{setsNumber},
           #{repsTarget},
           #{restTime},
           #{isCompleted},
           #{isDone}
       );

mapper.update:
UPDATE todos
SET is_completed = #{isCompleted}
WHERE todo_id = #{todoId};

UPDATE todos
SET is_done = #{isDone}
WHERE date = #{date};

mapper.select:
SELECT COUNT(*) > 0
FROM todos
WHERE date = #{date}
  AND is_done = TRUE;

SELECT COUNT(*) FROM todos
WHERE date = #{date}
  AND (is_completed IS NULL OR is_completed = FALSE)
