mapper.select:
SELECT COUNT(*)
FROM todos
WHERE date = #{date}
  AND exercise_id = #{exerciseId}
  AND user_id = #{userId};


SELECT MAX(t.todo_id)
FROM todos t
WHERE t.user_id = #{userId}
AND t.sets_number = 1
AND t.todo_id <= #{todoId}
AND t.exercise_id = (
    SELECT exercise_id FROM todos WHERE todo_id = #{todoId}
)
AND t.date = (
    SELECT date FROM todos WHERE todo_id = #{todoId}
    )


SELECT COALESCE(MAX(t.sets_number), 1)
FROM todos t
WHERE t.user_id = #{userId}
  AND t.date = (
    SELECT date FROM todos WHERE todo_id = #{baseTodoId}
    )
                             AND t.exercise_id = (
SELECT exercise_id FROM todos WHERE todo_id = #{baseTodoId}
    )
                                AND t.todo_id >= #{baseTodoId}
                                AND t.todo_id < (
SELECT COALESCE(
    MIN(t2.todo_id),
    999999999
    )
FROM todos t2
WHERE t2.user_id = t.user_id
  AND t2.date = t.date
  AND t2.exercise_id = t.exercise_id
  AND t2.sets_number = 1
  AND t2.todo_id > #{baseTodoId}
    )

SELECT is_completed
FROM todos
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

mapper.update:
UPDATE todos
SET is_completed = IFNULL(#{isCompleted}, FALSE),
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

UPDATE todos
SET reps_target = #{repsTarget},
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

UPDATE todos
SET sets_number = -(sets_number)
WHERE date = #{date}
  AND exercise_id = #{exerciseId}
  AND user_id = #{userId};

UPDATE todos t
    JOIN (
    SELECT
    todo_id,
    ROW_NUMBER() OVER (ORDER BY todo_id ASC) AS new_number
    FROM todos
    WHERE date = #{date}
    AND exercise_id = #{exerciseId}
    AND user_id = #{userId}
    ) ranked
ON t.todo_id = ranked.todo_id
    SET t.sets_number = ranked.new_number
WHERE t.date = #{date}
  AND t.exercise_id = #{exerciseId}
  AND t.user_id = #{userId};

mapper.delete:
DELETE FROM todos
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

mapper.select:
SELECT date, exercise_id
FROM todos
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

mapper.update:
UPDATE todos
SET is_done = IFNULL(#{isDone}, FALSE),
    updated_at = CURRENT_TIMESTAMP
WHERE date = #{date}
  AND user_id = #{userId};

mapper.select:
SELECT COUNT(*)
FROM todos
WHERE date = #{date}
  AND user_id = #{userId}
  AND is_done = TRUE;

mapper.update:
UPDATE todos
SET rest_time = #{restTime},
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

UPDATE todos
SET rest_time = NULL,
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

mapper.select:
SELECT COUNT(*)
FROM todos
WHERE date = #{date}
  AND user_id = #{userId};

SELECT COUNT(*)
FROM todos
WHERE date = #{date}
  AND user_id = #{userId}
  AND (is_completed IS NULL OR is_completed = FALSE);

mapper.update:
UPDATE todos
SET workout_id = #{workoutId}
WHERE todo_id = #{todoId}
  AND user_id = #{userId}

mapper.select:
SELECT workout_id
FROM todos
WHERE todo_id = #{todoId}
  AND user_id = #{userId}

SELECT COALESCE(MAX(sets_number), 0)
FROM todos
WHERE workout_id = #{workoutId}
  AND user_id = #{userId}
