mapper.select:
SELECT COUNT(*)
FROM todos
WHERE date = #{date}
  AND exercise_id = #{exerciseId};

mapper.update:
UPDATE todos
SET sets_number = -(sets_number)
WHERE date = #{date}
  AND exercise_id = #{exerciseId};

UPDATE todos t
    JOIN (
    SELECT
    todo_id,
    ROW_NUMBER() OVER (ORDER BY todo_id ASC) AS new_number
    FROM todos
    WHERE date = #{date}
    AND exercise_id = #{exerciseId}
    ) ranked
ON t.todo_id = ranked.todo_id
    SET t.sets_number = ranked.new_number
WHERE t.date = #{date}
  AND t.exercise_id = #{exerciseId};

mapper.select:
SELECT date, exercise_id
FROM todos
WHERE todo_id = #{todoId};

mapper.update:
UPDATE todos
SET is_done = IFNULL(#{isDone}, FALSE)
WHERE date = #{date};

mapper.select:
SELECT COUNT(*) FROM todos
WHERE date = #{date}
  AND (is_completed IS NULL OR is_completed = FALSE)

mapper.update:
UPDATE todos
SET rest_time = #{restTime},
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};

UPDATE todos
SET rest_time = NULL,
    updated_at = CURRENT_TIMESTAMP
WHERE todo_id = #{todoId}
  AND user_id = #{userId};
